const mongoose = require("mongoose");
const Schema = mongoose.Schema;
// Create Schema
const UserSchema = new Schema({
  name: {
    type: String,
    required: true
  },
  email: {
    type: String,
    required: true
  },
  password: {
    type: String,
    required: true
  },
  avatar : {
    type:String,
    default:'data:image/jpeg;base64,'
  }
 
});

// mongoose.model("users", UserSchema);

module.exports = User = mongoose.model("users", UserSchema);
